#!/usr/bin/env python3\n\"\"\"\n直接启动应用（跳过复杂测试）\n\"\"\"\n\nimport subprocess\nimport sys\nimport os\nfrom pathlib import Path\n\ndef main():\n    print(\"🚀 直接启动用户行为分析平台...\")\n    \n    # 检查数据文件\n    data_file = Path(\"data/events_ga4.ndjson\")\n    if not data_file.exists():\n        print(\"📊 生成测试数据...\")\n        try:\n            subprocess.run([sys.executable, \"generate_clean_data.py\"], check=True)\n            print(\"✅ 测试数据生成成功\")\n        except:\n            print(\"⚠️ 测试数据生成失败，但继续启动\")\n    \n    print(\"\\n🌐 启动 Streamlit 应用...\")\n    print(\"📝 应用地址: http://localhost:8501\")\n    print(\"⏹️  按 Ctrl+C 停止应用\")\n    print(\"\\n\" + \"=\" * 50)\n    \n    try:\n        # 直接启动 Streamlit\n        cmd = [sys.executable, \"-m\", \"streamlit\", \"run\", \"main.py\", \n               \"--server.port\", \"8501\"]\n        subprocess.run(cmd)\n    except KeyboardInterrupt:\n        print(\"\\n👋 应用已停止\")\n    except Exception as e:\n        print(f\"❌ 启动失败: {e}\")\n        print(\"\\n💡 尝试手动启动:\")\n        print(\"   streamlit run main.py\")\n\nif __name__ == \"__main__\":\n    main()"